        [Route("GetNewsDetail")]
        [AcceptVerbs("POST", "GET")]
        public ReturnModels<NewsDetail> GetNewsDetail([FromBody]ParamModel myParam)
        {
            MyClass<NewsModels> slx = new MyClass<NewsModels>();
            IMongoCollection<NewsModels> getCollection = slx.ConnectMongo("Mobile", "News");
            FilterDefinitionBuilder<NewsModels> builderFilter = Builders<NewsModels>.Filter;
            FilterDefinition<NewsModels> filter = builderFilter.Eq("uniquekey", myParam.id);
            NewsModels Data = getCollection.Find(filter).First();
            ReturnModels<NewsDetail> Result = new ReturnModels<NewsDetail>();
            Result.message = 1;
            Result.data_single = Data.Detail;
            Result.time = DateTime.Now.ToString();
            return Result;

        }


