        [Route("GetImagesContent")]
        [AcceptVerbs("POST", "GET")]
        public ReturnModels<ImagesList> GetImagesContent([FromBody]ParamModel myParam)
        {
            MyClass<ImagesModels> slx = new MyClass<ImagesModels>();
            IMongoCollection<ImagesModels> getCollection = slx.ConnectMongo("Mobile", "Images");
            ReturnModels<ImagesList> Result = new ReturnModels<ImagesList>();
            Result.message = 1;
            Result.time = DateTime.Now.ToString("yyyy-MM-dd hh:mm:ss");
            Result.data = new List<ImagesList>();
            if (myParam.id == "0")
            {
                List<ImagesModels> Data = getCollection.Find(FilterDefinition<ImagesModels>.Empty).ToList();
                foreach (ImagesModels item in Data)
                {
                   // Data.IndexOf(item);获取下标
                   if(item.Images!=null)
                    Result.data.AddRange(item.Images);
                }
            }
            else {
                FilterDefinitionBuilder<ImagesModels> builderFilter = Builders<ImagesModels>.Filter;
                FilterDefinition<ImagesModels> filter = builderFilter.Eq("groupid", myParam.id);
                ImagesModels Data = getCollection.Find(filter).First();
                Result.data = Data.Images;
            }
            return Result;
        }

        [Route("GetImagesDetail")]
        [AcceptVerbs("POST", "GET")]
        public ReturnModels<ImagesDetail> GetImagesDetail([FromBody]ParamModel myParam)
        {
            MyClass<ImagesModels> slx = new MyClass<ImagesModels>();
            IMongoCollection<ImagesModels> getCollection = slx.ConnectMongo("Mobile", "Images");
            FilterDefinitionBuilder<ImagesModels> builderFilter = Builders<ImagesModels>.Filter;

            FilterDefinition<ImagesModels> filter = builderFilter.And(builderFilter.Eq("groupid", myParam.gid),builderFilter.Eq("Images.pid", myParam.id));

            FilterDefinition<ImagesModels> filter2 = builderFilter.Eq("Images.pid", myParam.id);

            //  FilterDefinition<ImagesModels> filter2 = builderFilter.Eq("groupid", myParam.gid);
            ImagesModels Data = getCollection.Find(filter2).First();

            //using (var cursor = getCollection.Find(filter2).ToCursor())
