        }
        [Route("GetProductDetail")]
        [AcceptVerbs("POST", "GET")]
        public ReturnModels<Product_DetailModels> GetProductDetail([FromBody]ParamModel myParam)
        {
            var myClient = new MongoClient("mongodb://localhost:27017");
            IMongoDatabase myDB = myClient.GetDatabase("Mobile");
            IMongoCollection<Product_DetailModels> myCollection = myDB.GetCollection<Product_DetailModels>("Product_Detail");

            FilterDefinitionBuilder<Product_DetailModels> builderFilter = Builders<Product_DetailModels>.Filter;
            FilterDefinition<Product_DetailModels> filter = builderFilter.Eq("itemid", myParam.id);

            Product_DetailModels Data = myCollection.Find(filter).First();
            ReturnModels<Product_DetailModels> Result = new ReturnModels<Product_DetailModels>();
            Result.message = 1;
            Result.data_single = Data;
            Result.time = DateTime.Now.ToString();
            return Result;
        }
        [Route("GetCartItem")]
        [AcceptVerbs("POST", "GET")]
        public ReturnModels<Product_DetailModels> GetCartItem([FromBody]ParamModel myParam)
        {
            var myClient = new MongoClient("mongodb://localhost:27017");
            IMongoDatabase myDB = myClient.GetDatabase("Mobile");
            IMongoCollection<Product_DetailModels> myCollection = myDB.GetCollection<Product_DetailModels>("Product_Detail");

            List<Product_DetailModels> Data = new List<Product_DetailModels>();
            foreach (string item in myParam.id.Split(',')) {
                FilterDefinitionBuilder<Product_DetailModels> builderFilter = Builders<Product_DetailModels>.Filter;
                FilterDefinition<Product_DetailModels> filter = builderFilter.Eq("itemid", item);
                Product_DetailModels temp = myCollection.Find(filter).First();
                Data.Add(temp);
            }
            ReturnModels<Product_DetailModels> Result = new ReturnModels<Product_DetailModels>();
            Result.message = 1;
            Result.data = Data;
            Result.time = DateTime.Now.ToString();
            return Result;
        }





        //统一链接mongo工具类
        public class MyClass<T> {
            //通过自定义类来传递方法类型参数【泛型】
            public IMongoCollection<T> ConnectMongo(string Database,string Collection) {
                //连接mongo数据库
                var myClient = new MongoClient("mongodb://localhost:27017");
                IMongoDatabase myDB = myClient.GetDatabase(Database);
                IMongoCollection<T> myCollection = myDB.GetCollection<T>(Collection);
               
                return myCollection;
            }

            public FilterDefinition<T> EqFilter(string Field,string Key) {
                FilterDefinitionBuilder<T> builderFilter = Builders<T>.Filter;
                FilterDefinition<T> filter = builderFilter.Eq(Field, Key);
                return filter;
            }

        }
       



